const loadImage = (url) => {
    return new Promise((resolve, reject) => {
        const img = new Image();
        img.src = url;
        img.onload = () => resolve(url);
        img.onerror = (error) => {
            console.error(`Error loading image: ${url}`, error);
            reject(error);
        };
    });
};



